package p2023_07_12;

public class VariableEx03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("번호\t이름\t직업");	// \t 탭만큼 띄어쓰기
		System.out.println("행 단위 출력\n");		// \n 한줄(enter) 띄기		
		System.out.println("우리는 \"개발자\" 입니다."); // 글자 안에 "" 을 넣고 싶으면 \" \"을 넣어야 한다.
		System.out.println("봄\\여름\\가을\\겨울");   // 말안에 \이 넣고 싶으면 \\ 을 입력해야 한다.
	}

}
