package p2023_07_17;

public class Break01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 무한루프 : for문
		// break : 반복문을 빠져 나오는 역할
		
		for(int i =1; ;i++) {		//for문 무한루프 돌리는 방법
			System.out.println(i +"무한출력");
			if(i == 100)	break;
		}
		
	
		
	
		
		
	}

}
